mapper.select:
SELECT a.goods_no,
b.seq AS type_seq, b.goods_option_name, b.goods_type, b.list_sort, b.hidden_fl
FROM DOC5_GOODS a LEFT JOIN DOC5_OPTION_TYPE b ON a.goods_no = b.goods_no LEFT JOIN DOC5_GOODS_OPTION c ON b.seq = c.ot_seq LEFT JOIN DOC5_GOODS_INFO d ON a.goods_no = d.goods_no WHERE a.goods_no = #{goods_no}

SELECT a.goods_no,
c.seq AS option_seq, c.ot_seq, c.option_name, c.option_price, c.option_hidden
FROM DOC5_GOODS a LEFT JOIN DOC5_OPTION_TYPE b ON a.goods_no = b.goods_no LEFT JOIN DOC5_GOODS_OPTION c ON b.seq = c.ot_seq LEFT JOIN DOC5_GOODS_INFO d ON a.goods_no = d.goods_no WHERE a.goods_no = #{goods_no}
